import 'dart:ui';import 'package:bingo_wholesale/const/all_const.dart';import 'package:flutter/material.dart';import '../../data_models/enums/status_name.dart';import '../app_styles/text_styles.dart';import '../utils.dart';extension StatusExtension on StatusNames {  Widget toStatus(      {bool isIconAvailable = true,      TextStyle textStyle = TextStyles.statusCardStatus}) {    switch (this) {      case StatusNames.sale_pending_approval:        return statusWidget(            text: "Sale Pending Approval",            color: AppColors.statusReject,            textStyle: textStyle,            isIconAvailable: isIconAvailable);      case StatusNames.procesada:        return statusWidget(            text: "Procesada",            color: AppColors.statusVerified,            textStyle: textStyle,            isIconAvailable: isIconAvailable);      case StatusNames.sale_approval_delivered:        return statusWidget(            text: "Sale Approval/Delivered",            color: AppColors.statusVerified,            textStyle: textStyle,            isIconAvailable: isIconAvailable);      case StatusNames.pending:        return statusWidget(            text: "Pending",            color: AppColors.statusConfirmed,            textStyle: textStyle,            isIconAvailable: isIconAvailable);      case StatusNames.rejected:        return statusWidget(            text: "Rejected",            color: AppColors.statusReject,            textStyle: textStyle,            isIconAvailable: isIconAvailable);      case StatusNames.active:        return statusWidget(            text: "Active",            color: AppColors.statusVerified,            textStyle: textStyle,            isIconAvailable: isIconAvailable);      default:        return statusWidget(            text: "Error",            color: AppColors.fontAsh,            textStyle: textStyle,            isIconAvailable: isIconAvailable);    }  }}class StatusArgument {  String title;  Color color;  StatusArgument({this.title = "", this.color = AppColors.fontAsh});}