import 'package:bingo_wholesale/const/all_const.dart';import 'package:bingo_wholesale/const/app_assets.dart';import 'package:bingo_wholesale/const/app_colors.dart';import 'package:bingo_wholesale/const/app_styles/text_styles.dart';import 'package:flutter/material.dart';import '../data_models/enums/status_name.dart';statusWidget(    {Color color = AppColors.statusProgress,    String text = "",    bool isIconAvailable = true,    TextStyle textStyle = TextStyles.statusCardStatus}) {  return Row(    mainAxisAlignment: MainAxisAlignment.center,    children: [      if (isIconAvailable)        Image.asset(          AppAsset.statusIcon,          color: color,        ),      Text(        text,        style: textStyle.copyWith(color: color),      ),    ],  );}StatusNames statusNamesEnumFromServer(text) {  String newText = text.replaceAll(" ", "_").replaceAll("/", "_").toLowerCase();  print(newText);  return convertToStatusNames(newText);}getNiceText(String text,    {TextStyle style = TextStyles.dashboardHeadTitleAsh, bool nxtln = false}) {  List<String> name = text.split(':');  String restTest() {    String sub = "";    for (int i = 1; i < name.length - 0; i++) {      sub = sub + name[i];      if (i < name.length - 1) sub = sub + ":";    }    return sub;  }  return text.isNotEmpty      ? RichText(          text: TextSpan(            text: name[0] == "" ? "" : "${name[0]}:",            style: style,            children: <TextSpan>[              if (nxtln)                TextSpan(                  text: "\n",                ),              TextSpan(                text: restTest() == "" ? "" : restTest(),                style: style.copyWith(fontWeight: AppFontWeighs.regular),              ),            ],          ),        )      : SizedBox();}