import 'package:dartz/dartz.dart';import '../../data/repository_impl.dart';import '../models/country_list_model/country_list_model.dart';import '../models/failure.dart';import 'base_usecase.dart';class CountriesUseCase extends BaseUseCase<void, CountryListModel> {  final RepositoryImpl _repository;  CountriesUseCase(this._repository);  @override  Future<Either<Failure, CountryListModel>> execute(void input) async {    return await _repository.getCountries();  }}