import 'package:bingo_wholesale/data_models/models/store_model/store_model.dart';import 'package:dartz/dartz.dart';import '../../data/repository_impl.dart';import '../models/failure.dart';import 'base_usecase.dart';class StoreUseCase extends BaseUseCase<void, StoreModel> {  final RepositoryImpl _repository;  StoreUseCase(this._repository);  @override  Future<Either<Failure, StoreModel>> execute(void input) async {    return await _repository.getStores();  }}