import 'package:bingo_wholesale/const/all_const.dart';import 'package:flutter/material.dart';class CancelButton extends StatelessWidget {  Function()? onPressed;  bool active;  String text;  double width;  double height;  CancelButton({    this.onPressed,    this.text = "",    this.width = 283.0,    this.height = 30.0,    this.active = true,  });  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 7.0),      child: ElevatedButton(        style: ElevatedButton.styleFrom(          elevation: 0,          minimumSize: Size(width, height),          primary: AppColors.fontWhite,          onPrimary: AppColors.fontBlack,          shape: RoundedRectangleBorder(            side: const BorderSide(color: AppColors.fontBlack),            borderRadius: BorderRadius.circular(25),          ),        ),        onPressed: onPressed ?? () {},        child: Text(          text,          style: AppTextStyles.buttonText.copyWith(color: AppColors.fontBlack),        ),      ),    );  }}