import 'package:bingo_wholesale/const/all_const.dart';import 'package:bingo_wholesale/const/utils.dart';import 'package:flutter/material.dart';class AccountBalanceCard extends StatelessWidget {  String title;  List<String> subTitleList;  AccountBalanceCard({this.title = "", this.subTitleList = const []});  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 12.0),      child: Container(        padding: const EdgeInsets.all(25.0),        decoration: BoxDecoration(          color: AppColors.cardColor,          borderRadius: BorderRadius.circular(10.0),        ),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            getNiceText(title,                style: AppTextStyles.statusCardTitle                    .copyWith(fontWeight: AppFontWeighs.light)),            if (subTitleList.isNotEmpty)              Wrap(                children: [                  for (int i = 0; i < subTitleList.length; i++)                    SizedBox(                      width: 40.0.wp,                      child: getNiceText(subTitleList[i], nxtln: true),                    ),                ],              )          ],        ),      ),    );  }}