import 'package:auto_size_text/auto_size_text.dart';import 'package:bingo_wholesale/const/all_const.dart';import 'package:flutter/material.dart';class DashboardTilesCard extends StatelessWidget {  final Color color;  final String icon;  final String amount;  final String title;  DashboardTilesCard(      {this.color = AppColors.fontAsh,      this.icon = "assets/icons/ic_allocated_order.png",      this.amount = "\$45,445.90",      this.title = "Allocated Credits"});  @override  Widget build(BuildContext context) {    return Card(      child: Container(        height: 150.0,        width: 176.0,        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(15.0),          color: color,        ),        child: Stack(          children: [            Padding(              padding: const EdgeInsets.all(20.0),              child: Column(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  Image.asset(                    icon,                    width: 27.0,                    height: 40.0,                  ),                  Padding(                    padding: const EdgeInsets.symmetric(vertical: 8.0),                    child: AutoSizeText(                      amount,                      maxLines: 1,                      style: TextStyles.cartAmountText,                    ),                  ),                  AutoSizeText(                    title,                    maxLines: 1,                    style: TextStyles.cartTitleText,                  ),                ],              ),            ),            Positioned(              top: 20.0,              left: 48.0,              child: Image.asset(                AppAsset.cardShade,                height: 192.0,                width: 192.0,              ),            ),          ],        ),      ),    );  }}