import 'package:bingo_wholesale/const/all_const.dart';import 'package:bingo_wholesale/const/app_styles/app_box_decoration.dart';import 'package:flutter/material.dart';class SelectedDropdownField extends StatelessWidget {  SelectedDropdownField(      {required this.items,      this.dropdownValue = "",      this.fieldName = "",      this.hintText = "",      this.onChange});  defaultFunction(siteName) {}  List<String> items;  String dropdownValue;  String fieldName;  String hintText;  Function(String)? onChange;  @override  Widget build(BuildContext context) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Text(fieldName),        15.0.giveHeight,        Container(          decoration: AppBoxDecoration.borderDecoration,          height: 45.0,          child: ButtonTheme(            alignedDropdown: true,            padding: EdgeInsets.zero,            child: DropdownButtonHideUnderline(              child: DropdownButton<String>(                hint: Text(                  hintText,                  style: const TextStyle(                    fontSize: 12,                    color: Color(0xFFa5a5a5),                  ),                  textAlign: TextAlign.center,                ),                elevation: 0,                isDense: false,                isExpanded: true,                value: dropdownValue,                icon: const Icon(Icons.arrow_drop_down_outlined),                items: items.map((items) {                  return DropdownMenuItem<String>(                    value: items,                    child: Text(items),                  );                }).toList(),                onChanged: (String? newValue) {                  onChange!(newValue!) ?? () {};                },              ),            ),          ),        ),      ],    );  }}