import 'package:bingo_wholesale/const/all_const.dart';import 'package:flutter/material.dart';class ClassifiedText extends StatelessWidget {  String text1;  String text2;  ClassifiedText({this.text1 = "", this.text2 = ""});  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.only(bottom: 8.0),      child: RichText(        text: TextSpan(          text: text1,          style: AppTextStyles.dashboardHeadTitleAsh,          children: <TextSpan>[            TextSpan(              text: text2,              style: AppTextStyles.dashboardHeadTitleAsh                  .copyWith(fontWeight: AppFontWeighs.semiBold),            ),          ],        ),      ),    );  }}