part of '../../../ui/home_screen/home_screen_view.dart';class RetailerTabsInRequestTab extends StatelessWidget {  final HomeScreenViewModel model;  const RetailerTabsInRequestTab(this.model, {Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      color: AppColors.background,      height: 72,      child: Row(        mainAxisAlignment: MainAxisAlignment.center,        children: [          SubmitButton(            onPressed: () {              model.changeRequestTab(HomePageRequestTabs.associateRequest);            },            active:                model.requestTabTitle == HomePageRequestTabs.associateRequest                    ? true                    : false,            width: 114.0,            text: AppString.associationRequests,          ),          SubmitButton(            onPressed: () {              model.changeRequestTab(HomePageRequestTabs.creditLineRequest);            },            active:                model.requestTabTitle == HomePageRequestTabs.creditLineRequest                    ? true                    : false,            width: 114.0,            text: AppString.creditLineRequests,          ),        ],      ),    );  }}