part of '../../ui/add_credit_line_screen/add_credit_line_view.dart';class QuestionAnswerPart extends StatelessWidget {  QuestionAnswerPart(this.model, {Key? key}) : super(key: key);  AddCreditLineViewModel model;  @override  Widget build(BuildContext context) {    return Column(      children: [        const Text(          "Question answers",          style: AppTextStyles.dashboardBodyTitle,        ),        Divider(),        for (var i = 0; i < model.listOfQuistion.length; i++)          Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              NameTextField(                controller: model.answerController[i],                // controller: model.crp3Controller,                fieldName: model.listOfQuistion[i].question!,              ),              if (model.answerController[i].text.isEmpty)                const SubmitButton(                  width: 100.0,                  text: 'Reply',                ),              10.0.giveHeight,              Divider(),            ],          ),      ],    );  }}