part of '../../../ui/home_screen/home_screen_view.dart';class RetailerTabsInRequestTab extends StatelessWidget {  HomeScreenViewModel model;  RetailerTabsInRequestTab(this.model);  @override  Widget build(BuildContext context) {    return Container(      color: AppColors.background,      height: 72,      child: Row(        mainAxisAlignment: MainAxisAlignment.center,        children: [          SubmitButton(            onPressed: () {              model.changeRequestTab(0);            },            active: model.requestTabIndex == 0 ? true : false,            width: 114.0,            text: AppString.associationRequests,          ),          SubmitButton(            onPressed: () {              model.changeRequestTab(1);            },            active: model.requestTabIndex == 1 ? true : false,            width: 114.0,            text: AppString.creditLineRequests,          ),        ],      ),    );  }}