import 'package:bingo_wholesale/const/app_bar_titles.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:stacked/stacked.dart';import '../../../const/app_colors.dart';import 'add_association_request_screen_view_model.dart';class AddAssociationRequestScreenView extends StatelessWidget {  @override  Widget build(BuildContext context) {    return ViewModelBuilder<AddAssociationRequestScreenViewModel>.reactive(        viewModelBuilder: () => AddAssociationRequestScreenViewModel(),        builder: (context, model, child) {          return Scaffold(            appBar: AppBar(              backgroundColor: model.isRetailer                  ? AppColors.appBarColorRetailer                  : AppColors.appBarColorWholesaler,              title: Text(                AppBarTitles.addrequest,              ),            ),            body: Container(),          );        });  }}