import 'package:bingo_wholesale/repository/repository.dart';import 'package:bingo_wholesale/services/navigation/navigationService.dart';import 'package:stacked/stacked.dart';import '../../../app/locator.dart';import '../../../data_models/models/wholesaler_list_model/wholesaler_list_model.dart';import '../../../services/auth_service/auth_service.dart';class AddAssociationRequestScreenViewModel extends BaseViewModel {  final AuthService _authService = locator<AuthService>();  final Repository _repository = locator<Repository>();  final NavigationService _navigationService = locator<NavigationService>();  int wholeSalerOrFia = 0;  bool get isRetailer => _authService.isRetailer.value;  List<WholeSalerOrFiaListData> get wholeSaleList => _repository.wholeSaleList;  List<WholeSalerOrFiaListData> get fiaList => _repository.fiaList;  void changeTabBar(int i) {    wholeSalerOrFia = i;    notifyListeners();  }  List<String> selectedWholeSaler = [];  List<String> selectedFia = [];  void addRemoveWholesaler(int i) {    var data = selectedWholeSaler.contains(wholeSaleList[i].uniqueId);    if (data) {      selectedWholeSaler.remove(wholeSaleList[i].uniqueId!);    } else {      selectedWholeSaler.add(wholeSaleList[i].uniqueId!);    }    notifyListeners();  }  void addRemoveFia(int i) {    var data = selectedFia.contains(fiaList[i].uniqueId);    if (data) {      selectedFia.remove(fiaList[i].uniqueId!);    } else {      selectedFia.add(fiaList[i].uniqueId!);    }    notifyListeners();  }  void cancelButtonPressed() {    _navigationService.pop();  }  void sendWholesalerRequest() {    _repository.sendWholesalerRequest(selectedWholeSaler);    selectedWholeSaler.clear();  }  void sendFiaRequest() {    _repository.sendFiaRequest(selectedFia);    selectedFia.clear();  }}