import 'package:bingo_wholesale/app/locator.dart';import 'package:bingo_wholesale/app/router.dart';import 'package:bingo_wholesale/data_models/models/retailer_creditline_request_details_model/retailer_creditline_request_details_model.dart';import 'package:bingo_wholesale/repository/repository_retailer.dart';import 'package:bingo_wholesale/services/navigation/navigationService.dart';import 'package:flutter/cupertino.dart';import 'package:stacked/stacked.dart';import '../../../const/database_helper.dart';import '../../../data_models/construction_model/wholesaler_data.dart';import '../../../data_models/models/component_models/fie_list_creditline_request_model.dart';import '../../../services/local_data/table_names.dart';class AddCreditLineViewModel extends ReactiveViewModel {  AddCreditLineViewModel() {    getAllFieListForCreditLine();  }  final dbHelper = DatabaseHelper.instance;  NavigationService _navigationService = locator<NavigationService>();  RepositoryRetailer _repositoryRetailer = locator<RepositoryRetailer>();  bool acceptTermCondition = false;  bool isView = false;  int selectedOption = 10;  TextEditingController crn1Controller = TextEditingController();  TextEditingController crn2Controller = TextEditingController();  TextEditingController crn3Controller = TextEditingController();  TextEditingController crp1Controller = TextEditingController();  TextEditingController crp2Controller = TextEditingController();  TextEditingController crp3Controller = TextEditingController();  List<TextEditingController> _controller = [];  List<TextEditingController> get answerController => _controller;  List<FieCreditLineRequestData> _allFieCreditLine = [];  List<FieCreditLineRequestData> get allFieCreditLine => _allFieCreditLine;  List<FieQuestionAnswer> _listOfQuistion = [];  List<FieQuestionAnswer> get listOfQuistion => _listOfQuistion;  List<SupportedDocuments> _fileList = [];  List<SupportedDocuments> get fileList => _fileList;  List<WholesalersData> get creditLineInformation =>      _repositoryRetailer.creditLineInformation.value;  RetailerCreditLineReqDetailsModel _retailerCreditLineReqDetails =      RetailerCreditLineReqDetailsModel();  RetailerCreditLineReqDetailsModel get retailerCreditLineReqDetails =>      _retailerCreditLineReqDetails;  void changeAcceptTermCondition() {    acceptTermCondition = !acceptTermCondition;    notifyListeners();  }  void changeSelectedOption(int v) {    selectedOption = v;    print("v $v");    notifyListeners();  }  void getAllFieListForCreditLine() {    dbHelper.queryAllRows(TableNames.fieFistForCreditlineRequest).then((value) {      _allFieCreditLine =          value.map((d) => FieCreditLineRequestData.fromJson(d)).toList();      print(_allFieCreditLine);      notifyListeners();    });  }  void gotoAddWholesalerScreen() {    _navigationService.pushNamed(Routes.addWholesalerView);  }  void gotoUpdateWholesalerScreen(int index) {    WholesalersData data = WholesalersData(        id: creditLineInformation[index].id,        wholesaler: creditLineInformation[index].wholesaler,        currency: creditLineInformation[index].currency,        monthlyPurchase: creditLineInformation[index].monthlyPurchase,        averageTicket: creditLineInformation[index].averageTicket,        visitFrequency: creditLineInformation[index].visitFrequency,        amount: creditLineInformation[index].amount);    _navigationService.pushNamed(Routes.addWholesalerView, arguments: data);  }  @override  List<ReactiveServiceMixin> get reactiveServices => [_repositoryRetailer];  void setDetails(String arguments) async {    int numberOfAnswer = 0;    setBusy(true);    notifyListeners();    isView = true;    await _repositoryRetailer.getCreditLinesDetails(arguments);    _retailerCreditLineReqDetails =        _repositoryRetailer.retailerCreditLineReqDetails;    crn1Controller.text =        _retailerCreditLineReqDetails.data!.commercialNameOne!;    crn2Controller.text =        _retailerCreditLineReqDetails.data!.commercialNameTwo!;    crn3Controller.text =        _retailerCreditLineReqDetails.data!.commercialNameThree!;    crp1Controller.text =        _retailerCreditLineReqDetails.data!.commercialPhoneOne!;    crp2Controller.text =        _retailerCreditLineReqDetails.data!.commercialPhoneTwo!;    crp3Controller.text =        _retailerCreditLineReqDetails.data!.commercialPhoneThree!;    _listOfQuistion = _retailerCreditLineReqDetails.data!.fieQuestionAnswer!;    numberOfAnswer =        _retailerCreditLineReqDetails.data!.fieQuestionAnswer!.length;    _controller = List.generate(        numberOfAnswer,        (i) => TextEditingController(            text: _retailerCreditLineReqDetails                .data!.fieQuestionAnswer![i].answer));    _fileList = _retailerCreditLineReqDetails.data!.supportedDocuments!;    setBusy(false);    notifyListeners();  }  void submitAnswers() {    for (var i = 0; i < answerController.length; i++) {      print(answerController[i].text);    }  }}