import 'package:bingo_wholesale/services/navigation/navigationService.dart';import 'package:stacked/stacked.dart';import '../../../app/locator.dart';import '../../../services/auth_service/auth_service.dart';import '../../widgets/alert/activation_dialog.dart';class AssociationRequestDetailsScreenModel extends BaseViewModel {  final AuthService _authService = locator<AuthService>();  final NavigationService _navigationService = locator<NavigationService>();  bool get isRetailer => _authService.isRetailer.value;  void openActivationCodeDialog() {    _navigationService.displayDialog(const ActivationDialog());  }  List<String> selectionDataList = [    'Item 1',    'Item 2',    'Item 3',    'Item 4',    'Item 5',  ];  String selectedCustomerType = "Item 1";  String selectedGracePeriodGroups = "Item 1";  String selectedPricingGroups = "Item 1";  String selectedSalesZone = "Item 1";  String selectedAllowOrders = "Item 1";  String selectedCustomerSinceDate = "Item 1";  String selectedVisitFrequency = "Item 1";  void changeCustomerType(String data) {    selectedCustomerType = data;    notifyListeners();  }  void changeGracePeriodGroups(String data) {    selectedGracePeriodGroups = data;    notifyListeners();  }  void changePricingGroups(String data) {    selectedPricingGroups = data;    notifyListeners();  }  void changeSalesZone(String data) {    selectedSalesZone = data;    notifyListeners();  }  void changeAllowOrders(String data) {    selectedAllowOrders = data;    notifyListeners();  }  void changeCustomerSinceDate(String data) {    selectedCustomerSinceDate = data;    notifyListeners();  }  void changeVisitFrequency(String data) {    selectedVisitFrequency = data;    notifyListeners();  }  void gotoBackScreen() {    _navigationService.pop();  }}