import 'package:bingo_wholesale/services/storage/db.dart';import 'package:flutter/material.dart';import 'package:stacked/stacked.dart';import '../../../app/locator.dart';import '../../../services/api_service/api_service.dart';import '../../../services/storage/deviceStorage.dart';class DashboardScreenViewModel extends BaseViewModel    with WidgetsBindingObserver {  final DeviceStorage _deviceStorage = locator<DeviceStorage>();  final ApiService _apiService = locator<ApiService>();  DashboardScreenViewModel() {    _apiService.geStoreData();    print("token" + _deviceStorage.getString(DataBase.userToken));  }  int numberOfTabs = 5;  int selectedNumber = 0;  final double iconHeight = 24;  void onChangedTab(context, int index) {    selectedNumber = index;    DefaultTabController.of(context)!.animateTo(index);    notifyListeners();  }}