import 'package:bingo_wholesale/app/router.dart';import 'package:bingo_wholesale/const/app_bar_titles.dart';import 'package:bingo_wholesale/data_models/models/store_model/store_model.dart';import 'package:bingo_wholesale/repository/repository.dart';import 'package:stacked/stacked.dart';import '../../../app/locator.dart';import '../../../data/data_source/cards_properties_list.dart';import '../../../data/data_source/confirmation.dart';import '../../../data/data_source/invoices_data.dart';import '../../../data/data_source/recommandation_data.dart';import '../../../data_models/construction_model/confirmation_model/confirmation_model.dart';import '../../../data_models/construction_model/dashboard_card_properties_model/dashboard_card_properties_model.dart';import '../../../data_models/construction_model/invoice_model/invoice_model.dart';import '../../../data_models/construction_model/recommandation_deposite_model/recommandation_deposite_model.dart';import '../../../data_models/enums/home_page_bottom_tabs.dart';import '../../../data_models/models/association_request_model/association_request_model.dart';import '../../../data_models/models/user_model/user_model.dart';import '../../../services/auth_service/auth_service.dart';import '../../../services/navigation/navigationService.dart';class HomeScreenViewModel extends ReactiveViewModel {  final AuthService _authService = locator<AuthService>();  NavigationService _navigationService = locator<NavigationService>();  final Repository _repository = locator<Repository>();  List<ConfirmationModel> get confirmationData => confirmationDataMockList;  List<InvoiceModel> invoiceData = invoiceMock;  List<StoreData> get storeData => _repository.storeList;  List<AssociationRequestData> get associationRequestData =>      _repository.wholesalerList;  List<ReccomandationModel> recommadationData = recommadationDataMockUp;  UserModel get user => _authService.user.value;  HomePageBottomTabs homeScreenBottomTabs = HomePageBottomTabs.dashboard;  int requestTabIndex = 0;  int settingTabIndex = 2;  String appBarTitle = "DASHBOARD";  HomeScreenViewModel() {    // _apiService.getCountriesData();    _repository.getStores();    _repository.getWholesaler();    _repository.getFia();    _repository.getWholesalerAssociationData();  }  bool get isRetailer => _authService.isRetailer.value;  void gotoSalesDetailsScreen(ConfirmationModel data) {    _navigationService.pushNamed(Routes.salesDetailsScreen, arguments: data);  }  void changeSecondaryBottomTab(HomePageBottomTabs v) {    homeScreenBottomTabs = v;    switch (v) {      case HomePageBottomTabs.dashboard:        appBarTitle = AppBarTitles.dashBoard;        break;      case HomePageBottomTabs.requests:        appBarTitle = AppBarTitles.request;        break;      case HomePageBottomTabs.settings:        appBarTitle = AppBarTitles.settings;        break;      case HomePageBottomTabs.accountBalance:        appBarTitle = AppBarTitles.accountBalance;        break;      default:        appBarTitle = AppBarTitles.dashBoard;    }    notifyListeners();  }  void changeRequestTab(i) {    requestTabIndex = i;    notifyListeners();  }  void changeSettingTab(i) {    settingTabIndex = i;    notifyListeners();  }  void gotoAddNewRequest() {    _navigationService.pushNamed(Routes.addNewAssociationRequest);  }  void gotoAssociationRequestDetailsScreen() {    _navigationService.pushNamed(Routes.associationRequestDetailsScreen);  }  List<DashboardCardPropertiesModel> retailerCardsPropertiesList =      retailerCardsPropertiesListData;  List<DashboardCardPropertiesModel> wholesalerCardsPropertiesList =      wholesalerCardsPropertiesListData;  @override  List<ReactiveServiceMixin> get reactiveServices =>      [_authService, _repository];}