part of '../home_screen_view.dart';class RetailerConfirmationCard extends StatelessWidget {  ConfirmationModel confirmationData;  Function() function;  RetailerConfirmationCard(this.confirmationData, {required this.function});  @override  Widget build(BuildContext context) {    return InkWell(      onTap: function,      child: Container(        margin: const EdgeInsets.symmetric(vertical: 10.0),        padding: const EdgeInsets.symmetric(horizontal: 19.0, vertical: 15.0),        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(10.0),          border: Border.all(            color: AppColors.borderColors,            width: 1,          ),        ),        child: Column(          children: [            Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Text(                  confirmationData.invoice!,                  style: TextStyles.dashboardHeadTitle                      .copyWith(fontWeight: AppFontWeighs.semiBold),                ),                AutoSizeText(                  "USD ${confirmationData.rate}",                  maxLines: 1,                  style: TextStyles.dashboardHeadTitle.copyWith(                    fontWeight: AppFontWeighs.light,                  ),                ),              ],            ),            Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Column(                  crossAxisAlignment: CrossAxisAlignment.start,                  mainAxisSize: MainAxisSize.min,                  children: [                    Text(                      confirmationData.date!,                      style: TextStyles.dashboardBodyTitle,                    ),                    const SizedBox(                      height: 10,                    ),                    ClassifiedText(                        text1: 'Order ID: ',                        text2: '${confirmationData.orderId}'),                    ClassifiedText(                        text1: 'Invoice to:  ',                        text2: '${confirmationData.invoiceTo}'),                  ],                ),                Column(                  crossAxisAlignment: CrossAxisAlignment.start,                  mainAxisSize: MainAxisSize.max,                  children: [                    statusNamesEnumFromServer(confirmationData.status)                        .toStatus(),                    const SizedBox(                      height: 10,                    ),                    ClassifiedText(                        text1: 'FIE: ', text2: '${confirmationData.fie}'),                    ClassifiedText(                        text1: 'S. No: ', text2: '${confirmationData.sNo}'),                  ],                ),              ],            ),          ],        ),      ),    );  }}