part of '../home_screen_view.dart';class WholesalerInvoiceCards extends StatelessWidget {  InvoiceModel invoiceData;  WholesalerInvoiceCards(this.invoiceData);  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsets.only(bottom: 25.0),      padding: EdgeInsets.all(15.0),      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(10.0),        border: Border.all(color: AppColors.borderColors),      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              getNiceText("Invoiuce To: ${invoiceData.invoiceTo}", nxtln: true),              getNiceText("Date of Invoice: ${invoiceData.dateOfInvoice}",                  nxtln: true),              getNiceText("Amount: RD\$ ${invoiceData.amount}", nxtln: true),            ],          ),          SizedBox(            width: 35.0.wp,            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                statusNamesEnumFromServer(invoiceData.status).toStatus(),                getNiceText("Days to Payments: ${invoiceData.paymentDuration}",                    nxtln: true),                getNiceText("S.No: ${invoiceData.sNo}", nxtln: true),              ],            ),          ),        ],      ),    );  }}