import 'package:bingo_wholesale/const/all_const.dart';import 'package:bingo_wholesale/const/app_assets.dart';import 'package:bingo_wholesale/const/app_sizes/app_sizes.dart';import 'package:bingo_wholesale/const/app_styles/app_box_decoration.dart';import 'package:bingo_wholesale/presentation/widgets/buttons/submit_button.dart';import 'package:bingo_wholesale/presentation/widgets/text_fields/name_text_field.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:stacked/stacked.dart';import 'login_screen_view_model.dart';class LoginScreenView extends StatelessWidget {  const LoginScreenView({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ViewModelBuilder<LoginScreenViewModel>.reactive(      viewModelBuilder: () => LoginScreenViewModel(),      builder: (context, model, child) {        return Scaffold(          body: Stack(            children: [              SizedBox(                height: 100.0.hp,                width: 100.0.wp,                child: Image.asset(                  AppAsset.loginBg,                  fit: BoxFit.cover,                ),              ),              Center(                child: Padding(                  padding: AppPaddings.loginScreenMainCard,                  child: Card(                    child: Container(                      padding: AppPaddings.loginScreenInnerCard,                      // width: 280.0,                      decoration: AppBoxDecoration.loginScreenCardDecoration,                      child: Column(                        mainAxisSize: MainAxisSize.min,                        children: [                          SizedBox(                            width: 222,                            height: 73,                            child: Image.asset(                              AppAsset.loginLogo,                            ),                          ),                          15.0.giveHeight,                          NameTextField(                            controller: model.nameController,                            fieldName: AppString.emailAddredd,                          ),                          20.0.giveHeight,                          NameTextField(                            obscureText: true,                            controller: model.passwordController,                            fieldName: AppString.password,                          ),                          20.0.giveHeight,                          SubmitButton(                            height: 45.0,                            onPressed: model.login,                            text: AppString.login,                          ),                          20.0.giveHeight,                          Text(                            AppString.forgotPassword,                            style: AppTextStyles.loginTitleStyle,                          )                        ],                      ),                    ),                  ),                ),              ),            ],          ),        );      },    );  }}