import 'package:bingo_wholesale/const/all_const.dart';import 'package:bingo_wholesale/const/app_assets.dart';import 'package:bingo_wholesale/const/app_styles/app_box_decoration.dart';import 'package:bingo_wholesale/presentation/common_widgets/buttons/submit_button.dart';import 'package:bingo_wholesale/presentation/common_widgets/text_fields/name_text_field.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:stacked/stacked.dart';import 'login_screen_view_model.dart';class LoginScreenView extends StatelessWidget {  @override  Widget build(BuildContext context) {    return ViewModelBuilder<LoginScreenViewModel>.reactive(      viewModelBuilder: () => LoginScreenViewModel(),      builder: (context, model, child) {        return Scaffold(          body: Stack(            children: [              SizedBox(                height: 100.0.hp,                width: 100.0.wp,                child: Image.asset(                  AppAsset.loginBg,                  fit: BoxFit.cover,                ),              ),              Center(                child: Padding(                  padding: const EdgeInsets.symmetric(horizontal: 35.0),                  child: Card(                    child: Container(                      padding: EdgeInsets.symmetric(                        horizontal: 38.0,                        vertical: 20.0,                      ),                      // width: 280.0,                      decoration: AppBoxDecoration.loginScreenCardDecoration,                      child: Column(                        mainAxisSize: MainAxisSize.min,                        children: [                          SizedBox(                            width: 222,                            height: 73,                            child: Image.asset(                              AppAsset.loginLogo,                            ),                          ),                          const SizedBox(                            height: 15.0,                          ),                          NameTextField(                            controller: model.nameController,                            fieldName: AppString.email,                          ),                          const SizedBox(                            height: 20.0,                          ),                          NameTextField(                            controller: model.passwordController,                            fieldName: AppString.password,                          ),                          const SizedBox(                            height: 20.0,                          ),                          SubmitButton(                            height: 45.0,                            onPressed: model.login,                            text: AppString.login,                          ),                          const SizedBox(                            height: 20.0,                          ),                          Text(                            AppString.forgotPassword,                            style: AppTextStyles.loginTitleStyle,                          )                        ],                      ),                    ),                  ),                ),              ),            ],          ),        );      },    );  }}