import 'package:bingo_wholesale/data_models/models/user_model/user_model.dart';import 'package:bingo_wholesale/services/auth_service/auth_service.dart';import 'package:flutter/material.dart';import 'package:flutter/scheduler.dart';import 'package:stacked/stacked.dart';import '../../../app/locator.dart';import '../../../app/router.dart';import '../../../services/navigation/navigationService.dart';import '../../widgets/alert/alert_dialog.dart';class LoginScreenViewModel extends BaseViewModel {  LoginViewModel() {    SchedulerBinding.instance.addPostFrameCallback((_) {});  }  TextEditingController nameController = TextEditingController();  TextEditingController passwordController = TextEditingController();  final AuthService _authService = locator<AuthService>();  final NavigationService _navigationService = locator<NavigationService>();  void login() async {    try {      UserModel user = await _authService.login(          nameController.text, passwordController.text);      if (user.success!) {        _navigationService.pushNamed(Routes.dashboardScreen);      } else {        _navigationService.animatedDialog(AlertDialogMessage(user.message!));      }    } on Exception catch (e) {      // TODO    }  }}