import 'package:bingo_wholesale/services/auth_service/auth_service.dart';import 'package:flutter/material.dart';import 'package:flutter/scheduler.dart';import 'package:stacked/stacked.dart';import '../../../app/locator.dart';class LoginScreenViewModel extends BaseViewModel {  LoginViewModel() {    SchedulerBinding.instance.addPostFrameCallback((_) {});  }  TextEditingController nameController = TextEditingController();  TextEditingController passwordController = TextEditingController();  final AuthService _authService = locator<AuthService>();  void login() {    _authService.login(nameController.text, passwordController.text);  }}