import 'package:bingo_wholesale/const/allConst.dart';import 'package:flutter/material.dart';class SubmitButton extends StatelessWidget {  Function()? onPressed;  bool active;  String text;  double width;  double height;  Color color;  SubmitButton(      {this.onPressed,      this.text = "",      this.width = 283.0,      this.height = 30.0,      this.active = true,      this.color = AppColors.activeButtonColor});  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.symmetric(horizontal: 7.0),      child: ElevatedButton(        style: ElevatedButton.styleFrom(          minimumSize: Size(width, height),          primary: active ? color : AppColors.inactiveButtonColor,          onPrimary: AppColors.fontWhite,          shape: RoundedRectangleBorder(            borderRadius: BorderRadius.circular(25),          ),        ),        onPressed: onPressed ?? () {},        child: Text(          text,          style: TextStyles.buttonText.copyWith(              color: active ? AppColors.fontWhite : AppColors.fontBlack),        ),      ),    );  }}