import 'package:bingo_wholesale/const/all_const.dart';import 'package:bingo_wholesale/const/app_styles/app_box_decoration.dart';import 'package:flutter/material.dart';import 'package:shimmer/shimmer.dart';import '../../../const/app_sizes/app_sizes.dart';class ShimmerScreen extends StatelessWidget {  int number;  ShimmerScreen({this.number = 1, Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      child: Column(        mainAxisAlignment: MainAxisAlignment.start,        children: [          for (var j = 0; j < number; j++)            Padding(              padding: AppPaddings.screenARDSWidgetPadding,              child: Container(                margin: AppMargins.screenARDSWidgetMarginH,                padding: AppPaddings.screenARDSWidgetInnerPadding,                decoration: AppBoxDecoration.shadowBox,                width: 100.0.wp,                height: 200.0,                child: Shimmer.fromColors(                  baseColor: AppColors.shimmer1,                  highlightColor: AppColors.shimmer2,                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    mainAxisAlignment: MainAxisAlignment.spaceAround,                    children: [                      for (var i = 1; i < 5; i++)                        Container(                          height: 15.0,                          width: 20.0.wp * i,                          decoration: AppBoxDecoration.shadowBox,                          child: Text(""),                        ),                    ],                  ),                ),              ),            ),        ],      ),    );  }}