import 'package:bingo_wholesale/app/router.dart';import 'package:bingo_wholesale/services/navigation/navigationService.dart';import 'package:bingo_wholesale/services/storage/deviceStorage.dart';import 'package:flutter/material.dart';import '../../../app/locator.dart';class MyDrawer extends StatelessWidget {  MyDrawer({Key? key}) : super(key: key);  var _deviceStorage = locator<ZDeviceStorage>();  var _navigationService = locator<NavigationService>();  void logout() {    _deviceStorage.clearData();    _navigationService.pushReplacementNamed(Routes.login);  }  @override  Widget build(BuildContext context) {    return RaisedButton(      onPressed: logout,      child: Text("Logout"),    );  }}