import 'package:bingo_wholesale/const/all_const.dart';import 'package:flutter/material.dart';class NameTextField extends StatelessWidget {  final String fieldName;  final TextEditingController? controller;  NameTextField({Key? key, this.controller, this.fieldName = ""})      : super(key: key);  @override  Widget build(BuildContext context) {    return SizedBox(      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Text(fieldName),          10.0.giveHeight,          SizedBox(            height: 45.0,            child: TextField(              decoration: AppInputStyles.ashOutlineBorder,              controller: controller,            ),          ),        ],      ),    );  }}