import 'package:bingo_wholesale/const/allConst.dart';import 'package:flutter/material.dart';class NameTextField extends StatelessWidget {  String fieldname;  TextEditingController controller;  NameTextField({required this.controller, this.fieldname = ""});  @override  Widget build(BuildContext context) {    return SizedBox(      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Text(fieldname),          SizedBox(            height: 15.0,          ),          TextField(            decoration: InputStyles.blackOutlineBorder,            controller: controller,          )        ],      ),    );  }}