import 'package:connectivity_plus/connectivity_plus.dart';import 'package:flutter/material.dart';import 'package:injectable/injectable.dart';@lazySingletonclass ConnectivityService {  Connectivity connectivity = Connectivity();  connectionStream() {    return StreamBuilder(        stream: Connectivity().onConnectivityChanged,        builder: (BuildContext context, snapshot) {          var connectivityResult = snapshot.data;          if (connectivityResult != null &&              connectivityResult == ConnectivityResult.none) {            return connectionWidget();          } else {            return Container();          }        });  }  connectionWidget() {    // return Container();    return Container(      margin: EdgeInsets.all(20.0),      alignment: Alignment.centerLeft,      height: 70.0,      width: double.infinity,      padding: EdgeInsets.symmetric(horizontal: 15.0),      decoration: BoxDecoration(        // color: AppTheme.colorError.withOpacity(0.8),        borderRadius: BorderRadius.circular(10.0),      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Container(              height: 30.0,              width: 30.0,              alignment: Alignment.center,              child: Stack(                children: [                  Icon(                    Icons.wifi_off,                    color: Colors.white,                    size: 28.0,                  ),                ],              )),          SizedBox(width: 15),          Flexible(            child: Text(              "Constants.message_no_net",            ),          )        ],      ),    );  }}