import 'package:injectable/injectable.dart';import '../../const/database_helper.dart';@lazySingletonclass LocalData {  final dbHelper = DatabaseHelper.instance;  void insert(tableName, tableData) async {    for (int i = 0; i < tableData.length; i++) {      await dbHelper.insert(tableName, tableData[i].toJson());    }  }  void delete(tableName) async {    dbHelper.deleteDB(tableName);  }}